import { useState } from 'react'

const Statistics = ({good, neutral, bad}) => {
  
  let sumFeedb = isNaN(good + neutral + bad) ? 0 : good + neutral + bad
  let sumFeedb2 = good + neutral + bad
  console.log("sumFeedb ", sumFeedb)
  console.log("sumFeedb2 ", sumFeedb2)
  let averageFeedb = (good + neutral + bad) / 3
  let positiveFeedb2 = good / sumFeedb * 100
  let positiveFeedb = sumFeedb = 0 ? 0 : good / sumFeedb * 100
  console.log("positiveFeedb ", positiveFeedb)
  console.log("positiveFeedb2 ", positiveFeedb2)

  return (
    <div>
      <h1>statistics</h1>
      <p>good: {good}</p>
      <p>neutral: {neutral}</p>
      <p>bad: {bad}</p>
      <p>all: {sumFeedb}</p>
      <p>average: {averageFeedb}</p>
      <p>positive: {positiveFeedb} %</p>
    </div>
  )

}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <h1>give feedback</h1>
      <button onClick={() => setGood(good + 1)}>good</button>
      <button onClick={() => setNeutral(neutral + 1)}>neutral</button>
      <button onClick={() => setBad(bad + 1)}>bad</button>
      <Statistics good={good} neutral={neutral} bad={bad} />
    </div>
  )
}

export default App
